#!/usr/bin/env python# -*- coding: utf-8 -*-from .send_email import MonitEmialSenderfrom .send_message import MonitMessageSenderclass Monit():		@classmethod	def send_message(self, module, title = '日志出现错误', phone_list=None):		monitor_msg = MonitMessageSender()		monitor_msg._send_err_message(module, title, phone_list)		@classmethod	def send_email(cls, module, title, receiver_email=None):		monitor_email = MonitEmialSender()		monitor_email._send_err_email(module, title, receiver_email)		